mapper.update:
update orderdb set 
o_option=#{o_option}, o_optiondetail=#{o_optiondetail}, o_optionprice=#{o_optionprice}, o_quantity=#{o_quantity}
where o_num=#{o_num}

update orderdb set 
o_paymentstatus=#{o_paymentstatus}, o_deliverystatus=#{o_deliverystatus}, o_reviewstatus=#{o_reviewstatus}, o_date=now(), o_number=#{o_number}, c_num=#{c_num}
where o_num=#{o_num}
